mapper.update:
UPDATE auto_login
SET expiration_at = DATE_ADD(NOW(), INTERVAL #{day} DAY)
WHERE auto_key = #{key}
  AND expiration_at > NOW()
  AND expiration_flag = FALSE
    LIMIT 1;

mapper.select:
SELECT uid, DATE_FORMAT(rdt, '%Y.%m.%d') AS rdt FROM whattodo_user
WHERE nm = #{nm}
  AND contact = ${contact};
